mapper.select:
SELECT 

FROM t_cw_bxd a

WHERE a.id = #{id}

SELECT 

FROM t_cw_bxd a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.bx_time = #{bxTime}


		AND a.bx_time between #{bxStartDate} and  #{bxEndDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_cw_bxd a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_cw_bxd SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
